import GameComponent from "../../../../GameObject/GameComponent";
import Cubes from "./Cubes";
import React from "react";
import Timer from "../../../../Timer/Timer";
import { WithWorld } from "../../../../World/HOC/WithWorld";
import Vector from "../../../../Vector/Vector";

class Tetris extends GameComponent {
  constructor(props) {
    super(props);
    this.children = [];

    for (let i = 0; i < 10; ++i) this.addCubes();
  }

  componentDidMount() {
    super.componentDidMount();
    this.addCubes();
    console.log(this.components);
    this.downCube();
  }

  cubeIndex = -1;
  downCube = () => {
    if (++this.cubeIndex >= this.components.length) return;
    this.components[++this.cubeIndex].rigidBody.gravity = 1;
    setInterval(this.downCube, 5000);
  };

  addCubes = () => {
    this.children.push(
      React.createElement(Cubes, {
        parent: this,
        gravity: 0,
        position: new Vector([40, -100]),

        name: "Cubes"
      })
    );
    this.update();
    console.log(this.children);
  };
}

export default WithWorld(Tetris);
