import GameComponent from "../../../../GameObject/GameComponent";
import React from "react";
import Cube from "./Cube";
import Vector from "../../../../Vector/Vector";
import { WithWorld } from "../../../../World/HOC/WithWorld";
import RigidBody from "../../../../GameObject/RigidBody";
import VectorUtilities from "../../../../VectorUtilities/VectorUtilities";
import CollisionManger from "../../../../CollisionManager/CollisionManager";

function randomIntFromInterval(min, max) {
  // min and max included
  return Math.floor(Math.random() * (max - min + 1) + min);
}

class Cubes extends GameComponent {
  constructor(props) {
    super(props);
    this.children = [React.createElement(RigidBody, { weight: 10, drag: 0.5 })];

    for (let i = 0; i < 4; ++i) {}
  }

  componentWillMount() {
    this.children.push(
      React.createElement(Cube, {
        offset: new Vector(),
        name: "Cube"
      })
    );
    for (let i = 0, length = 3; i < length; ++i) {
      const randomCube = this.components[
        randomIntFromInterval(1, this.components.length - 1)
      ];

      while (true) {
        let offset;

        switch (randomIntFromInterval(0, 3)) {
          case 0:
            offset = new Vector([10, 0]);
            break;
          case 1:
            offset = new Vector([-10, 0]);
            break;
          case 2:
            offset = new Vector([0, 10]);
            break;
          case 3:
            offset = new Vector([0, -10]);
            break;
          default:
            throw new Error("shit");
        }
        if (!this.collisionDetected()) {
          this.children.push(
            React.createElement(Cube, {
              offset: offset.plus(randomCube.props.offset),
              name: "Cube"
            })
          );
        }
      }
    }
    super.componentWillMount();
  }
  collisionDetected() {
    for (let i = 2; i < this.components.length; i += 2) {
      console.log("collisionDetextion");
      const component = this.components[i];
      const component2 = this.components[i - 1];
      if (
        CollisionManger.instance.collisionDetected(
          component.collisionZones[0],
          component2.collisionZones[0]
        )
      ) {
        return true;
      }
    }
    return false;
  }
}

export default WithWorld(Cubes);
